import "./App.css";
import useMyLocation from "./hooks/useMyLocation";

function App() {
	//useMyLocation returns location info, error and loading state
	const { locationInfo, error, loading } = useMyLocation();

	return (
		<div className="App">
			{loading ? (
				<h1>Loading...</h1>
			) : error?.message === "" ? (
				<div>
					<h1>
						{"Lat: " +
							locationInfo.lat +
							" Lon: " +
							locationInfo.lon}
					</h1>
					<h2>{"Accuracy in meters: " + locationInfo.accuracy}</h2>
				</div>
			) : (
				<h1>{error.message}</h1>
			)}

			<iframe
				src="https://www.google.com/maps/embed?pb=!1m10!1m8!1m3!1d739.761933933792!2d24.7316172!3d42.1278613!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2sbg!4v1705320138328!5m2!1sen!2sbg"
				width="300"
				height="225"
				allowfullscreen=""
				loading="lazy"
				referrerpolicy="no-referrer-when-downgrade"
			></iframe>
		</div>
	);
}

export default App;
