import { useEffect, useState } from "react";

//From these options we can manage how to get the user location
//If enableHighAccuracy is true it returns the user location more accurate.
//maximumAge is a option that controls how to get the location from the cache.(0 means that we don't take location from cache).
const OPTIONS = { enableHighAccuracy: true, maximumAge: 0 };

const useMyLocation = () => {
	const [locationInfo, setLocationInfo] = useState({
		lon: 0,
		lat: 0,
		accuracy: 0,
	});
	const [error, setError] = useState({ message: "" });
	const [loading, setLoading] = useState(false);

	//Update states if the geolocation info is returned successfully
	const onSuccess = (position) => {
		const freshLon = position.coords.longitude;
		const freshLat = position.coords.latitude;
		const accuracy = position.coords.accuracy;

		//If the coordinates are changed update them
		if (freshLon !== locationInfo?.lon || freshLat !== locationInfo?.lat) {
			setLocationInfo({ lon: freshLon, lat: freshLat, accuracy });
		}
		setLoading(false);
	};

	const onError = (error) => {
		setError(error);
		setLoading(false);
	};

	const getMyLocationHandler = async () => {
		try {
			//Check if the browser supports geolocation
			if (navigator.geolocation) {
				setLoading(true);

				//Get information about the location permissions of the browser.
				const permissions = await navigator.permissions.query({
					name: "geolocation",
				});

				if (
					permissions.state === "granted" ||
					permissions.state === "prompt"
				) {
					//watchPosition returns the user location when the device is moved
					navigator.geolocation.watchPosition(
						onSuccess,
						onError,
						OPTIONS
					);
				} else {
					//If perrmissions state is denied throw an error
					throw new Error(
						`Location permission state is ${permissions.state}. Please give permissions to access your location.`
					);
				}
			} else {
				throw new Error(
					"Geolocation is not supported by this browser."
				);
			}
		} catch (e) {
			setError(e);
			setLoading(false);
		}
	};

	useEffect(() => {
		getMyLocationHandler();
	}, []);

	return {
		locationInfo,
		error,
		loading,
	};
};

export default useMyLocation;
