import { useEffect, useState } from "react";
import "./App.css";

function App() {
	const [lon, setLon] = useState();
	const [lat, setLat] = useState();
	const [accuracy, setAccuracy] = useState();
	const [error, setError] = useState({ message: null });

	const OPTIONS = { enableHighAccuracy: true, maximumAge: 0, timeout: 40000 };

	const loading = !lon || !lat ? true : false;
	//Set geolocation coordinates
	const setCoordinates = (lon, lat, accuracy) => {
		setLon(lon);
		setLat(lat);
		setAccuracy(accuracy);
	};

	const getLocationSuccessfully = (position) => {
		try {
			const freshLong = position.coords.longitude.toFixed(7);
			const freshLat = position.coords.latitude.toFixed(7);
			const accuracy = position.coords.accuracy.toFixed(7);

			//If the coordinates are changed update them
			if (freshLong !== lon || freshLat !== lat) {
				setCoordinates(freshLong, freshLat, accuracy);
			}
		} catch (e) {
			setError(e);
		}
	};

	const getLocationIssue = (e, timer) => {
		console.log(timer);
		clearInterval(timer);
		setError(e);
	};

	const handleStartInterval = async () => {
		const permissions = await navigator.permissions.query({
			name: "geolocation",
		});
		console.log(permissions);
		if (permissions.state === "granted") {
			const timer = setInterval(() => {
				navigator.geolocation.getCurrentPosition(
					getLocationSuccessfully,
					(error) => getLocationIssue(error, timer),
					OPTIONS
				);
			}, 5000);

			return () => {
				clearInterval(timer);
			};
		}

		if (permissions.state === "prompt") {
			navigator.geolocation.getCurrentPosition(
				getLocationSuccessfully,
				getLocationIssue,
				OPTIONS
			);
		}
	};

	useEffect(() => {
		handleStartInterval();
	});

	const handledLog = error?.message
		? error.message
		: loading
		? "Loading..."
		: "Lon: " + lon + " | " + "Lat: " + lat;

	return (
		<div className="App">
			<h1>{handledLog}</h1>
			<h1>{" Accuracy: " + accuracy}</h1>
		</div>
	);
}

export default App;
