import { useEffect, useState } from "react";
import "./App.css";

function App() {
	const [long, setLong] = useState();
	const [lat, setLat] = useState();
	const [error, setError] = useState();

	const loading = !long || !lat ? true : false;

	const setCoordinates = (long, lat) => {
		setLong(long);
		setLat(lat);
	};

	const getLocationSuccessfuly = (position) => {
		const freshLong = position.coords.longitude.toFixed(7);
		const freshLat = position.coords.latitude.toFixed(7);

		if (freshLong !== long || freshLat !== lat) {
			setCoordinates(freshLong, freshLat);
		}
	};

	const getLocationIssue = (e) => {
		const freshLong = position.coords.longitude.toFixed(7);
		const freshLat = position.coords.latitude.toFixed(7);

		if (freshLong !== long || freshLat !== lat) {
			setCoordinates(freshLong, freshLat);
		}
	};

	useEffect(() => {
		const timer = setInterval(() => {
			navigator.geolocation.getCurrentPosition(getLocationSuccessfuly);
		}, 1000);

		return () => {
			clearInterval(timer);
		};
	}, []);

	return (
		<div className="App">
			<h1>
				{loading
					? "Loading..."
					: "long: " + long + " | " + "Lang: " + lat}
			</h1>
		</div>
	);
}

export default App;
