import "./App.css";
import useMyLocation from "./hooks/useMyLocation";

function App() {
	const { locationInfo, error, loading } = useMyLocation();
	return (
		<div className="App">
			{loading ? (
				<h1>Loading...</h1>
			) : !error?.message ? (
				<div>
					<h1>
						{"Lat: " +
							locationInfo.lat +
							" Lon: " +
							locationInfo.lon}
					</h1>
					<h2>{"Accuracy in meters: " + locationInfo.accuracy}</h2>
				</div>
			) : (
				<h1>{error.message}</h1>
			)}
		</div>
	);
}

export default App;
