import { useEffect, useState } from "react";
import "./App.css";

function App() {
	const [long, setLong] = useState();
	const [lat, setLat] = useState();
	const [error, setError] = useState({ message: null });

	const loading = !long || !lat ? true : false;

	const setCoordinates = (long, lat) => {
		setLong(long);
		setLat(lat);
	};

	const getLocationSuccessfully = (position) => {
		const freshLong = position.coords.longitude;
		const freshLat = position.coords.latitude;
		console.log(position.coords);

		setCoordinates(freshLong, freshLat);
	};

	const getLocationIssue = (e) => {
		setError(e);
	};

	useEffect(() => {
		try {
			if ("geolocation" in navigator) {
				addEventListener(
					"devicemotion",
					(e) => {
						console.log(e);
					},
					(err) => console.log(err)
				);
			} else {
				throw new Error("Geolocation is not supported");
			}
		} catch (error) {
			setError(error);
		}
	}, []);

	const handledLog = error?.message
		? error.message
		: loading
		? "Loading..."
		: "Long: " + long + " | " + "Lat: " + lat;

	return (
		<div className="App">
			<h1>{handledLog}</h1>
		</div>
	);
}

export default App;
